let currentTab=0;function showTab(e){currentTab=e,prev=document.querySelector("#prevBtn"),next=document.querySelector("#nextBtn"),submit=document.querySelector("#submitBtn");const t=document.querySelectorAll(".tab-pane");t.forEach(e=>{e.classList.remove("is-active")}),t[currentTab].classList.add("is-active"),currentTab==0?document.getElementById("prevBtn").style.visibility="hidden":document.getElementById("prevBtn").style.visibility="visible",currentTab==t.length-1?(next.style.display="none",submit.style.display="inline",submit.style.visibility="visible"):(submit.style.display="none",next.style.display="inline",next.style.visibility="visible");const n=document.querySelectorAll(".tab-link");var s=0,o=0;n.forEach(e=>{e.classList.remove("is-active"),n[currentTab]==e?o=s:s+=e.offsetWidth}),document.querySelector(".tabs").scrollLeft=o,n[currentTab].classList.add("is-active")}function nextTab(){showTab(currentTab+1)}function prevTab(){showTab(currentTab-1)}window.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("prevBtn");e.addEventListener("click",prevTab);const t=document.getElementById("nextBtn");t.addEventListener("click",nextTab);const n=document.getElementById("submitBtn");n.addEventListener("click",function(){document.querySelector("#questionnaire-form").submit()}),document.querySelectorAll(".tab-link").forEach(e=>{e.addEventListener("click",function(){showTab(parseInt(e.getAttribute("data-id")))})}),showTab(currentTab)})