function initYoutube(){for(var t=document.querySelectorAll(".youtube"),e=0;e<t.length;e++)t[e].addEventListener("click",function(){var e=document.createElement("iframe");e.setAttribute("frameborder","0"),e.setAttribute("allowfullscreen",""),e.setAttribute("allow","autoplay"),e.setAttribute("src","https://www.youtube-nocookie.com/embed/"+this.dataset.id+"?rel=0&showinfo=0&autoplay=1"),this.innerHTML="",this.appendChild(e)})}document.addEventListener("DOMContentLoaded",initYoutube),initYoutube()